package objects;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlRootElement;/** * Created by Uno on 20.10.2016. */@XmlRootElement(name = "erpData")public class Activemqmessage {    private int customernumber;    private int materialnumber;    private String ordernumber;    private String timestamp;    /*public Activemqmessage(int customernumber, int materialnumber, String ordernumber, String timestamp) {        this.customernumber = customernumber;        this.materialnumber = materialnumber;        this.ordernumber = ordernumber;        this.timestamp = timestamp;    }*/    public int getCustomernumber() {        return customernumber;    }    @XmlElement(name = "customerNumber")    public void setCustomernumber(int customernumber) {        this.customernumber = customernumber;    }    public int getMaterialnumber() {        return materialnumber;    }    @XmlElement(name = "materialNumber")    public void setMaterialnumber(int materialnumber) {        this.materialnumber = materialnumber;    }    public String getOrdernumber() {        return ordernumber;    }    @XmlElement(name = "orderNumber")    public void setOrdernumber(String ordernumber) {        this.ordernumber = ordernumber;    }    public String getTimestamp() {        return timestamp;    }    @XmlElement(name = "timeStamp")    public void setTimestamp(String timestamp) {        this.timestamp = timestamp;    }    public String toString(){        String string =                "CustomerNumber: "+ this.getCustomernumber()+                        "\nMaterialNumber: "+this.getMaterialnumber()+                        "\nOrderNumber: "+this.getOrdernumber()+                        "\nTimeStamp: "+this.getTimestamp()+                        "\n";        return string;    }}